INSERT INTO `aldryn_newsblog_article_translation` VALUES (1,'cs','Žákovská liga 2017/2018','zakovska-liga-2017-2018','<p>V uplynulém školním roce 2017/2018 jsme se počtvrté pokusili vybrat a ocenit tři „nejlepší“ žáky naší školy. Protože je těžké rozhodnout, kdo je vlastně nejlepší, vsadili jsme na všestrannost. Použili jsme stejná kritéria jako loni, kritéria byla známa již na začátku školního roku. Podle nich byli žáci hodnoceni z několika odlišných hledisek. Každý žák měl možnost získat body za docházku, prospěch, reprezentaci školy v soutěžích, práci pro třídu či pro školu. Vzali jsme v úvahu i mimoškolní aktivity žáků nebo zisk odborných certifikátů.</p>','','','','',3),(2,'cs','Vítěz celostátní soutěže v programování','vitez-celostatni-souteze-v-programovani-je-z-nasi-skoly','<p>Velkého úspěchu dosáhl žák třídy IT2 <font color=\"#0000FF\">Denis Kurka</font>, který ziskem 101 bodů ovládl kategorii <font color=\"#0000FF\">Programování mikrořadičů</font> v ústředním kole prestižní programátorské soutěže, jež proběhlo ve dnech 22. až 24. června 2018 na Technické univerzitě v Liberci.</p>','Vítězství v celostátní soutěži v programování','Student oboru informační technologie zvítězil v ústředním kole soutěže v programování','soutěž, programování','',4);
